INSERT INTO `product_translations` (`id`, `product_id`, `title`, `slug`, `short`, `body`, `body2`, `locale`) VALUES
(1416, 1452, 'NIXON SHADOW WORLD TRAVELER BACKPACK SW', 'nixon-shadow-world-traveler-backpack-sw', 'Ranac', '<p>Ruksak za putovanja s kompresijskom pozadinom. Oblikovane i obloene naramenice. Neoprenske ploe za jednostavno pakiranje. Unutra&scaron;njost Death Star detalji na prednjoj ploi. Mogunost jednostavnog odvajanja unutar glavnog pretinca, ovisno o potrebama. Depovi za dodatke. Obloen pretinac za laptop. Unutarnji pretinac za tablet i razne dodatke. Dra za kljueve. Na prednjoj strani nalazi se vanjski vertikalni pretinac i na pozadini vanjski pretinac. Star Wars paket.</p>\r\n\r\n<p>DONJI DIO: 1680D balistini najlon</p>\r\n\r\n<p>PODSTAVA: 210D najlon s teksturom</p>\r\n\r\n<p>DIMENZIJA: 12&Prime; x 6.4&Prime; x 9.5&Prime;</p>\r\n\r\n<p>OBUJAM: 24&nbsp;</p>\r\n', '', 'sr'),
(1417, 1453, 'NIXON SHOWOFF LEATHER WALLET SW', 'nixon-showoff-leather-wallet-sw', 'Novanik', '<p>Sklopiv koni novanik s teksturom i detaljima Star Wars. Logotip ugraviran u unutra&scaron;njosti. Pretinac za kreditne kartice. Ugravirana podstava 210D. Na prednjoj strani isklesan logotip na konoj etiketi.</p>\r\n\r\n<p>DIMENZIJA: 10,8 x 9,2 cm</p>\r\n', '', 'sr'),
(1418, 1454, 'NIXON SHOWOFF LEATHER WALLET SW', 'nixon-showoff-leather-wallet-sw', 'Novanik', '<p>Sklopiv koni novanik s teksturom i detaljima Star Wars. Logotip ugraviran u unutra&scaron;njosti. Pretinac za kreditne kartice. Ugravirana podstava 210D. Na prednjoj strani isklesan logotip na konoj etiketi.</p>\r\n\r\n<p>DIMENZIJA: 10,8 x 9,2 cm</p>\r\n', '', 'sr'),
(1419, 1455, 'NIXON WATERLOCK BACKPACK SW', 'nixon-waterlock-backpack-sw', 'Ranac', '<p>DONJI DIO: 600D poliester</p>\r\n\r\n<p>PODSTAVA: 210D poliester s teksturom</p>\r\n\r\n<p>DIMENZIJA: 12&Prime; x 7.5&Prime; x 19.3&Prime;</p>\r\n\r\n<p>OBUJAM: 28 l</p>\r\n', '', 'sr'),
(1420, 1456, 'NIXON DRUM BACKPACK', 'nixon-drum-backpack', 'Ranac', '<p>Vrlo lagan ruksak s moguno&scaron;u uvijanja i otvorom na vrhu napravljen od 70D protkanog rip stop najlona. Sadri patent na vrhu i pretinac s patentom pri dnu. Lagano obloene naramenice i stranja ploa. Remenje za zatezanje po stranama. S detaljem tkane oznake loga.</p>\r\n\r\n<p>DONJI DIO: 600D poliester</p>\r\n\r\n<p>PODSTAVA: 210D najlon s teksturom</p>\r\n\r\n<p>DIMENZIJE: 28 x 28 x 48cm</p>\r\n\r\n<p>OBUJAM: 25 l</p>\r\n', '', 'sr'),
(1421, 1457, 'NIXON DRUM BACKPACK', 'nixon-drum-backpack', 'Ranac', '<p>Ranac</p>\r\n', '', 'sr'),
(1422, 1458, 'NIXON BASE BACKPACK', 'nixon-base-backpack', 'Ranac', '<p>Ruksak u obliku ploe s velikim glavnim odjeljkom, koni elementi za otvaranje pretinaca i unutarnja organizacija. Pretinac za laptop, podloene naramenice i pozadina. Prednji pretinac za modne dodatke s konim elementima za zatvaranje. Silikonski prsten s logotipom za prikljuenje dodataka. Gornja ruka umotana u kou.</p>\r\n\r\n<p>DONJI DIO: 1680D balistini najlon</p>\r\n\r\n<p>PODSTAVA: 210D najlon s grubom teksturom</p>\r\n\r\n<p>DIMENZIJA: 28 x 15,2 x 45,7cm</p>\r\n\r\n<p>OBUJAM: 19 l</p>\r\n', '', 'sr'),
(1423, 1459, 'NIXON BASE BACKPACK', 'nixon-base-backpack', 'Ranac', '<p>Rusak pravokutnog oblika s velikim glavnim pretincem, koni zip zatvarai i unutarnja organizacija. Gornji pretinac za laptop sa zip zatvaraem. Naramenice su podebljane, poleina ima mreastu prozranu podlogu. Prednji otvor ima koni remen za otvaranje. Logotip je otisnut na silikonsku trakicu za koju se mogu zakaiti razni modni i funkcionalni dodaci. Gornja ruka zamotana u kou. Logotip je otisnut na kou, a zastava na naramenicu.</p>\r\n\r\n<p>DONJI DIO: 1680D balistini najlon</p>\r\n\r\n<p>PODSTAVA: 210D najlon s teksturom</p>\r\n\r\n<p>DIMENZIJA: 28 x 15,2 x 45,7cm</p>\r\n\r\n<p>OBUJAM: 19 l</p>\r\n', '', 'sr'),
(1424, 1460, 'NIXON BASE RUKSAK', 'nixon-base-ruksak', 'Ruksak', '<p>Ruksak u pravokutnom obliku koristi PU zguvano platno na gornjem dijelu, podstava 1680 balistiki najlon. Glavni veliki otvor ima koni remen za otvaranje, a unutara&scaron;njost je organizirana s depom za sat. Sa strane ima zip otvor za laptop. Naramenice su podebljane, poleina ima mreastu prozranu podlogu. Prednji otvor ima koni remen za otvaranje. Gornji logotip je otisnut na silikonsku trakicu za koju se mogu zakaiti razni modni i funkcionalni dodaci. Gornja ruka zamotana u kou. Donji logotip je otisnut na kou, a zastava na naramenicu.</p>\r\n\r\n<p>VANJSKI MATERIJAL: Zguvano PU platno</p>\r\n\r\n<p>PODSTAVA: 210D logotipom proeti najlon</p>\r\n\r\n<p>DONJI DIO: 1680D balistiki najlon</p>\r\n\r\n<p>DIMENZIJA: 11&rdquo; x 6&rdquo; x 18&rdquo;</p>\r\n\r\n<p>OBUJAM: 19 litara</p>\r\n\r\n<p>BOJA: uta</p>\r\n', '', 'sr'),
