INSERT INTO `product_translations` (`id`, `product_id`, `title`, `slug`, `short`, `body`, `body2`, `locale`) VALUES
(1416, 1452, 'NIXON SHADOW WORLD TRAVELER BACKPACK SW', 'nixon-shadow-world-traveler-backpack-sw', 'Ranac', '<p>Ruksak za putovanja s kompresijskom pozadinom. Oblikovane i obložene naramenice. Neoprenske ploče za jednostavno pakiranje. Unutra&scaron;njost Death Star detalji na prednjoj ploči. Mogućnost jednostavnog odvajanja unutar glavnog pretinca, ovisno o potrebama. Džepovi za dodatke. Obložen pretinac za laptop. Unutarnji pretinac za tablet i razne dodatke. Držač za ključeve. Na prednjoj strani nalazi se vanjski vertikalni pretinac i na pozadini vanjski pretinac. Star Wars paket.</p>\r\n\r\n<p>DONJI DIO: 1680D balistični najlon</p>\r\n\r\n<p>PODSTAVA: 210D najlon s teksturom</p>\r\n\r\n<p>DIMENZIJA: 12&Prime; x 6.4&Prime; x 9.5&Prime;</p>\r\n\r\n<p>OBUJAM: 24&nbsp;</p>\r\n', '', 'sr'),
(1417, 1453, 'NIXON SHOWOFF LEATHER WALLET SW', 'nixon-showoff-leather-wallet-sw', 'Novčanik', '<p>Sklopiv kožni novčanik s teksturom i detaljima Star Wars. Logotip ugraviran u unutra&scaron;njosti. Pretinac za kreditne kartice. Ugravirana podstava 210D. Na prednjoj strani isklesan logotip na kožnoj etiketi.</p>\r\n\r\n<p>DIMENZIJA: 10,8 x 9,2 cm</p>\r\n', '', 'sr'),
(1418, 1454, 'NIXON SHOWOFF LEATHER WALLET SW', 'nixon-showoff-leather-wallet-sw', 'Novčanik', '<p>Sklopiv kožni novčanik s teksturom i detaljima Star Wars. Logotip ugraviran u unutra&scaron;njosti. Pretinac za kreditne kartice. Ugravirana podstava 210D. Na prednjoj strani isklesan logotip na kožnoj etiketi.</p>\r\n\r\n<p>DIMENZIJA: 10,8 x 9,2 cm</p>\r\n', '', 'sr'),
(1419, 1455, 'NIXON WATERLOCK BACKPACK SW', 'nixon-waterlock-backpack-sw', 'Ranac', '<p>DONJI DIO: 600D poliester</p>\r\n\r\n<p>PODSTAVA: 210D poliester s teksturom</p>\r\n\r\n<p>DIMENZIJA: 12&Prime; x 7.5&Prime; x 19.3&Prime;</p>\r\n\r\n<p>OBUJAM: 28 l</p>\r\n', '', 'sr'),
(1420, 1456, 'NIXON DRUM BACKPACK', 'nixon-drum-backpack', 'Ranac', '<p>Vrlo lagan ruksak s mogućno&scaron;ću uvijanja i otvorom na vrhu napravljen od 70D protkanog rip stop najlona. Sadrži patent na vrhu i pretinac s patentom pri dnu. Lagano obložene naramenice i stražnja ploča. Remenje za zatezanje po stranama. S detaljem tkane oznake loga.</p>\r\n\r\n<p>DONJI DIO: 600D poliester</p>\r\n\r\n<p>PODSTAVA: 210D najlon s teksturom</p>\r\n\r\n<p>DIMENZIJE: 28 x 28 x 48cm</p>\r\n\r\n<p>OBUJAM: 25 l</p>\r\n', '', 'sr'),
(1421, 1457, 'NIXON DRUM BACKPACK', 'nixon-drum-backpack', 'Ranac', '<p>Ranac</p>\r\n', '', 'sr'),
(1422, 1458, 'NIXON BASE BACKPACK', 'nixon-base-backpack', 'Ranac', '<p>Ruksak u obliku ploče s velikim glavnim odjeljkom, kožni elementi za otvaranje pretinaca i unutarnja organizacija. Pretinac za laptop, podložene naramenice i pozadina. Prednji pretinac za modne dodatke s kožnim elementima za zatvaranje. Silikonski prsten s logotipom za priključenje dodataka. Gornja ručka umotana u kožu.</p>\r\n\r\n<p>DONJI DIO: 1680D balistični najlon</p>\r\n\r\n<p>PODSTAVA: 210D najlon s grubom teksturom</p>\r\n\r\n<p>DIMENZIJA: 28 x 15,2 x 45,7cm</p>\r\n\r\n<p>OBUJAM: 19 l</p>\r\n', '', 'sr'),
